/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import Upload from './';
import Thumb from './thumbnail';
import { Playground } from 'docz';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "图片处理"
    }}>{`图片处理`}</h1>
    <p>{`对于图片的选择处理从选择到上传这个过程中可能会有以下步骤需要我们操作，上传图片我们首先想到的是 `}<inlineCode parentName="p">{`<input type="file" />`}</inlineCode>{`。`}<a parentName="p" {...{
        "href": "https://html.spec.whatwg.org/#file-upload-state-(type=file"
      }}>{`https://html.spec.whatwg.org/#file-upload-state-(type=file`}</a>{`)`}</p>
    <h2 {...{
      "id": "选择图片"
    }}>{`选择图片`}</h2>
    <p>{`基于 `}<inlineCode parentName="p">{`input[type=file]`}</inlineCode>{` 需要注意以下几个点`}</p>
    <ul>
      <li parentName="ul">{`Content Attributes: 支持的图片类型 `}<inlineCode parentName="li">{`accept="images/*"`}</inlineCode>{`，是否支持多选 `}<inlineCode parentName="li">{`multiple`}</inlineCode>{`，form 表单中是否必须 `}<inlineCode parentName="li">{`required`}</inlineCode></li>
      <li parentName="ul">{`Events: change and input`}</li>
      <li parentName="ul">{`IDL Attributes: `}<inlineCode parentName="li">{`files`}</inlineCode>{` 和 `}<inlineCode parentName="li">{`value`}</inlineCode></li>
      <li parentName="ul">{`Methods: `}<inlineCode parentName="li">{`select()`}</inlineCode></li>
    </ul>
    <h2 {...{
      "id": "预览图片"
    }}>{`预览图片`}</h2>
    <p><a parentName="p" {...{
        "href": "https://stackoverflow.com/questions/2303690/resizing-an-image-in-an-html5-canvas"
      }}>{`图片如何压缩`}</a><br parentName="p"></br>{`
`}<a parentName="p" {...{
        "href": "https://stackoverflow.com/questions/7584794/accessing-jpeg-exif-rotation-data-in-javascript-on-the-client-side/20600869"
      }}>{`图片预设方向`}</a><br parentName="p"></br>{`
`}<img alt=" " src="https://i.stack.imgur.com/VGsAj.gif" /></p>
    <h2 {...{
      "id": "裁剪图片"
    }}>{`裁剪图片`}</h2>
    <p><a parentName="p" {...{
        "href": ""
      }}></a></p>
    <h1 {...{
      "id": "演示"
    }}>{`演示`}</h1>
    <h2 {...{
      "id": "选择"
    }}>{`选择`}</h2>
    <Playground __position={0} __code={'class Example extends React.Component {\n    beforeUpload(e) {\n      if (\n        e.size > 10 ||\n        (/images\\//.test(e.type) && !/\\.ico|gif/.test(e.type))\n      ) {\n        return false\n      } else {\n        return true\n      }\n    }\n    onChange(file) {\n      /*Thumb.getImageOrientation(file)\n  .then(orientation => {\n    console.log(orientation);\n  })*/\n      /*const fr = new FileReader();\nfr.onload = e => {\n  console.log(e.target.result)\n};\nfr.readAsDataURL(file);*/\n      URL = URL || webkitURL\n      const url = URL.createObjectURL(file)\n      const img = document.createElement(\'img\')\n      img.src = url\n      img.onload = function() {\n        URL.revokeObjectURL(url)\n        console.log(url)\n      }\n      document.body.append(img)\n    }\n\n    render() {\n      return (\n        <div>\n          <Upload\n            accept=\"images/*\"\n            multiple\n            onChange={this.onChange}\n            beforeUpload={this.beforeUpload}\n          />\n          <Thumb ref=\"thumb\" />\n          上传文件\n        </div>\n      )\n    }\n  }'} __scope={{
      props: this ? this.props : props,
      Upload,
      Thumb,
      Playground
    }} __codesandbox={`undefined`} mdxType="Playground">
      {class Example extends React.Component {
        beforeUpload(e) {
          if (e.size > 10 || /images\//.test(e.type) && !/\.ico|gif/.test(e.type)) {
            return false;
          } else {
            return true;
          }
        }

        onChange(file) {
          /*Thumb.getImageOrientation(file)
            .then(orientation => {
              console.log(orientation);
            })*/

          /*const fr = new FileReader();
          fr.onload = e => {
            console.log(e.target.result)
          };
          fr.readAsDataURL(file);*/
          URL = URL || webkitURL;
          const url = URL.createObjectURL(file);
          const img = document.createElement('img');
          img.src = url;

          img.onload = function () {
            URL.revokeObjectURL(url);
            console.log(url);
          };

          document.body.append(img);
        }

        render() {
          return <div>
    <Upload accept="images/*" multiple onChange={this.onChange} beforeUpload={this.beforeUpload} mdxType="Upload" />
    <Thumb ref="thumb" mdxType="Thumb" />
    上传文件
  </div>;
        }

      }}
    </Playground>
    <h1 {...{
      "id": "api"
    }}>{`API`}</h1>
